+.connect-section {
+  width: 100%;
+  padding: 60px 20px;
+  background-color: #F9F7F5;
+  box-sizing: border-box;
+}
+
+.connect-top {
+  max-width: 1200px;
+  margin: 0 auto 60px auto;
+  text-align: center;
+}
+
+.connect-top h1 {
+  font-size: 45px;
+  margin-bottom: 16px;
+  color: #333;
+  font-weight: bold;
+  margin-top: 10px;
+}
+
+.connect-top p {
+  font-size: 19px;
+  color: #666;
+  max-width: 750px;
+  margin: 0 auto;
+}
+
+.connect-boxes {
+  display: flex;
+  flex-wrap: wrap;
+  justify-content: center;
+  gap: 16px;
+  margin-top: 40px;
+}
+
+.connect-box {
+  flex: 1 1 200px;
+  border: 1px solid #ccc;
+  border-radius: 12px;
+  padding: 16px;
+  font-size: 14px;
+  background-color: #fff;
+  box-shadow: 0 2px 5px rgba(0,0,0,0.05);
+  transition: all 0.3s ease;
+}
+
+.connect-box:hover {
+  box-shadow: 0 4px 10px rgba(0,0,0,0.1);
+}
+
+/* Bottom Section */
+.connect-bottom {
+  background-color: #FFFFFF;
+  padding: 60px 20px;
+  border-radius: 20px;
+  max-width: 1100px;
+  margin: 0 auto;
+  text-align: center;
+  box-shadow: inset 0 0 8px rgba(0,0,0,0.05);
+}
+
+.connect-bottom h2 {
+  font-size: 28px;
+  color: #333;
+  margin-bottom: 16px;
+}
+
+.connect-bottom p {
+  font-size: 15px;
+  color: #666;
+  max-width: 600px;
+  margin: 0 auto 32px auto;
+}
+
+.connect-buttons {
+  display: flex;
+  flex-wrap: wrap;
+  justify-content: center;
+  gap: 20px;
+}
+
+.connect-btn {
+  border: 1px solid #ccc;
+  padding: 14px 24px;
+  border-radius: 12px;
+  background-color: #fff;
+  font-size: 14px;
+  color: #444;
+  cursor: pointer;
+  transition: box-shadow 0.3s ease;
+}
+
+.connect-btn:hover {
+  box-shadow: 0 4px 10px rgba(0,0,0,0.1);
+}
+
+/* Responsive */
+@media (max-width: 768px) {
+  .connect-top h2,
+  .connect-bottom h2 {
+    font-size: 22px;
+  }
+
+  .connect-boxes,
+  .connect-buttons {
+    flex-direction: column;
+    gap: 12px;
+  }
+
+  .connect-btn {
+    width: 100%;
+  }
+}
