+body{
+  padding-top: 60px;
+}
+.hero-image-only {
+  position: relative;
+  width: 100%;
+  margin-top: 0;
+  padding: 0;
+}
+
+.hero-image-only img {
+  width: 100%;
+  height: auto;
+  display: block;
+  object-fit: cover;
+}
+
+/* overlay color */
+.hero-image-only .overlay {
+  position: absolute;
+  top: 0;
+  left: 0;
+  width: 100%;
+  height: 100%;
+  background: linear-gradient(
+    to bottom,
+    rgba(206, 97, 43, 0.9),
+    rgba(206, 97, 43, 0.3)
+  );
+  backdrop-filter: blur(1px);
+  z-index: 1;
+  pointer-events: none;
+}
+
+/* hero content */
+.hero-content {
+  position: absolute;
+  top: 50%;
+  left: 50%;
+  transform: translate(-50%, -55%);
+  z-index: 2;
+  text-align: center;
+  color: white;
+  padding: 0 1rem;
+}
+
+.hero-content h1 {
+  font-size: 4.5rem;
+  font-weight: bold;
+  /* margin-bottom: 0.2rem; */
+  /* line-height: 1.2; */
+  line-height: 1.1; /* slightly tighter line height*/
+}
+
+.highlight-text {
+  color: white;
+  font-size: 4.5rem;
+  margin: 0;
+}
+
+.hero-content p {
+  font-size: 4.5rem;
+  color: #ED8E5E;
+  white-space: nowrap;
