commits.content:
.hero-container {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 80px 60px;
  background: #fff;
  gap: 50px;
  flex-wrap: wrap;
}

.hero-content {
  flex: 1;
  max-width: 500px;
}

.hero-content h1 {
  font-size: 42px;
  color: #2f2121;
  margin-bottom: 20px;
}

.hero-content p {
  font-size: 18px;
  color: #8a6969;
  margin-bottom: 30px;
}

.hero-btn {
  padding: 12px 28px;
  background-color: #2f2121;
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 16px;
  cursor: pointer;
}

.hero-btn:hover {
  background-color: #473030;
}

.hero-image {
  flex: 1;
  display: flex;
  justify-content: center;
}

.hero-image img {
  max-width: 100%;
  height: auto;
  border-radius: 12px;
  object-fit: cover;
}
