import React from "react";
import "./HeroSection.css";
import heroImg from "../assets/hero-image.webp"; // make sure this image exists

const HeroSection = () => {
  return (
    <section className="hero-container">
      <div className="hero-content">
        <h1>Connect Over Coffee</h1>
        <p>Find meaningful connections and spark real conversations.</p>
        <button className="hero-btn">Get Started</button>
      </div>
      <div className="hero-image">
        <img src={heroImg} alt="Coffee chat" />
      </div>
    </section>
  );
};

export default HeroSection;
