.chat-box {
  width: 75%;
  display: flex;
  flex-direction: column;
  background-color: #fff;
}

.chat-header {
  padding: 16px;
  background-color: #2c3e50;
  color: #fff;
  font-size: 18px;
  font-weight: bold;
}

.chat-messages {
  flex: 1;
  padding: 16px;
  overflow-y: auto;
  background-color: #f9f9f9;
}

.chat-input {
  display: flex;
  border-top: 1px solid #ccc;
  padding: 12px 16px;
  background-color: #fff;
}

.chat-input input {
  flex: 1;
  padding: 10px;
  font-size: 14px;
  border: 1px solid #ccc;
  border-radius: 8px;
  margin-right: 10px;
}

.chat-input button {
  background-color: #3498db;
  color: white;
  border: none;
  padding: 10px 16px;
  border-radius: 8px;
  cursor: pointer;
  font-weight: bold;
}

.chat-input button:hover {
  background-color: #2980b9;
}
