/* messages.css */

.messages-page {
  display: flex;
  height: calc(100vh - 60px); /* if navbar height is 60px */
}

.sidebar {
  width: 250px;
  background-color: #f5f5f5;
  border-right: 1px solid #ddd;
  padding: 10px;
  overflow-y: auto;
}

.sidebar h2 {
  font-size: 18px;
  margin-bottom: 15px;
  font-weight: bold;
}

.conversation-item {
  display: flex;
  align-items: center;
  padding: 8px;
  border-radius: 8px;
  cursor: pointer;
  margin-bottom: 6px;
}

.conversation-item:hover,
.conversation-item.active {
  background-color: #e0e0e0;
}

.conversation-item img {
  width: 30px;
  height: 30px;
  margin-right: 10px;
}

.chat-box {
  flex: 1;
  padding: 15px;
  display: flex;
  flex-direction: column;
  background-color: #fff;
  overflow-y: auto;
}

.message-item {
  max-width: 60%;
  margin-bottom: 10px;
  padding: 10px 15px;
  border-radius: 15px;
  font-size: 14px;
  line-height: 1.4;
  word-wrap: break-word;
}

.message-item.left {
  background-color: #eee;
  align-self: flex-start;
}

.message-item.right {
  background-color: #cce4ff;
  align-self: flex-end;
}

.navbar {
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 60px;
  padding: 0 20px;
  background-color: #fff;
  border-bottom: 1px solid #ddd;
}
