.messenger-container {
  display: flex;
  height: 100vh;
  overflow: hidden;
}

/* Default Desktop: sidebar + chatbox side by side */
.sidebar {
  width: 300px;
  flex-shrink: 0;
  border-right: 1px solid #ccc;
}

.chatbox {
  flex: 1;
  width: 100%;
  overflow-y: auto;
}

/* ðŸ”» RESPONSIVE BELOW 1024px */
@media (max-width: 1024px) {
  .messenger-container {
    flex-direction: column;
  }

  .sidebar {
    width: 100%;
    border-right: none;
    border-bottom: 1px solid #ccc;
  }

  .chatbox {
    width: 100%;
    height: 100%;
  }
}
